import React, { useState, useEffect } from "react";
import "./ImageGallery.css";

const ImageGallery = ({ onClose }) => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Función para obtener las imágenes desde el backend
  const fetchImages = async () => {
    try {
      const res = await fetch("http://localhost:5288/api/Imagenes"); // Asegúrate de que esta URL sea correcta
      if (!res.ok) throw new Error("Error al obtener las imágenes");
      const data = await res.json();
      setImages(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Llamar a fetchImages cuando el componente se monte
  useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div className="image-gallery-modal">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Imágenes Cargadas</h2>
          {/* Asegúrate de que el onClose sea llamado correctamente */}
          <button onClick={onClose} className="close-btn">Cerrar</button>
        </div>

        <div className="modal-body">
          {loading ? (
            <p>Cargando imágenes...</p>
          ) : error ? (
            <p style={{ color: "red" }}>Error: {error}</p>
          ) : (
            <div className="gallery-grid">
              {images.length > 0 ? (
                images.map((image) => (
                  <div key={image.idImagen} className="image-item">
                    <img
                      src={`http://localhost:5288/api/Imagenes/${image.idImagen}`} // Asegúrate de que la URL para obtener la imagen sea correcta
                      alt={image.nombre}
                      style={{ maxWidth: "100%", maxHeight: "100%", objectFit: "cover" }}
                    />
                  </div>
                ))
              ) : (
                <p>No hay imágenes cargadas</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ImageGallery;
