import React, { useState, useEffect } from "react";
import "./ImageGallery.css";

const ImageGallery = ({ onClose, onSelect }) => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchImages = async () => {
    try {
      const res = await fetch("http://localhost:5288/api/Imagenes");
      if (!res.ok) throw new Error("Error al obtener las imágenes");
      const data = await res.json();
      setImages(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div className="image-gallery-modal">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Imágenes Cargadas</h2>
          <button onClick={onClose} className="close-btn">Cerrar</button>
        </div>

        <div className="modal-body">
          {loading ? (
            <p>Cargando imágenes...</p>
          ) : error ? (
            <p style={{ color: "red" }}>Error: {error}</p>
          ) : (
            <div className="gallery-grid">
              {images.length > 0 ? (
                images.map((image) => (
                  <div
                    key={image.idImagen}
                    className="image-item"
                    onClick={() => onSelect(image)}
                    style={{
                      cursor: "pointer",
                      border: "2px solid transparent",
                      borderRadius: "8px",
                      transition: "border 0.3s",
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.border = "2px solid #e2923c"}
                    onMouseLeave={(e) => e.currentTarget.style.border = "2px solid transparent"}
                  >
                    <img
                      src={`data:image/png;base64,${image.datosImagenBase64 || ""}`}
                      alt={image.nombre}
                      title={image.nombre}
                      style={{
                        maxWidth: "100%",
                        maxHeight: "100px",
                        objectFit: "contain",
                        borderRadius: "4px"
                      }}
                    />
                    <p style={{ fontSize: "0.9rem", textAlign: "center", marginTop: "0.5rem" }}>
                      {image.nombre}
                    </p>
                  </div>
                ))
              ) : (
                <p>No hay imágenes cargadas</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ImageGallery;
